import React, { Component } from 'react';  
import ReactTable from "react-table";  
import "react-table/react-table.css";  


class App extends Component {
   constructor(props) {
      super(props) 
      this.state = { 
         students: [
            { id: 1, name: 'Wasif', Business_unit: "Apps NA",Courses_Joined: 2, Course_name: "Java and Phython fundamentals",location: "bangalore",email: 'wasif@email.com' },
            { id: 2, name: 'Saleem', Business_unit: "Apps One", Courses_Joined: 1, Course_name: "Phython",location: "Mumbai",email: 'ali@email.com' },
            { id: 3, name: 'Sam', Business_unit: "EU",Courses_Joined: 3, Course_name: "JavaScript, Jquery and React", location: "chennai", email: 'saad@email.com' },
            { id: 4, name: 'kumar', Business_unit: "NA",Courses_Joined: 4, Course_name: "JavaScript,venilaScript,vueJS and Angular", location: "Hydrabad", email: 'kumar@gmail.com' },
            { id: 5, name: 'Joseph', Business_unit: "APPS NA",Courses_Joined: 2, Course_name: "JavaScript and Angular", location: "Hydrabad", email: 'joseph34@email.com' },
            { id: 6, name: 'Rahman', Business_unit: "NA",Courses_Joined: 1, Course_name: "spring and Hybernate", location: "Hydrabad", email: 'rahmank@email.com' },
            { id: 7, name: 'Hari', Business_unit: "EU",Courses_Joined: 2, Course_name: "Java and spring", location: "Mumbai", email: 'harirocky@email.com' },
            { id: 8, name: 'Anbhu', Business_unit: "NA",Courses_Joined: 1, Course_name: "Java", location: "Hydrabad", email: 'anbukesar@email.com' },
            { id: 9, name: 'Ankit', Business_unit: "APPS ONE",Courses_Joined: 2, Course_name: "JavaScript and Angular", location: "Chennai", email: 'ankit987@email.com' },
            { id: 10, name: 'Panchal', Business_unit: "EU",Courses_Joined: 3, Course_name: "JavaScript,typescript and Angular", location: "Bangalore", email: 'panchal123@email.com' }         
         ]
      }
   }
   renderTableData() {
    return this.state.students.map((student, index) => {
       const { id, name, Business_unit,Courses_Joined,Course_name,location, email } = student 
       return (
          <tr key={id}>
             <td>{id}</td>
             <td>{name}</td>
             <td>{Business_unit}</td>
             <td>{Courses_Joined}</td>
             <td>{Course_name}</td>
             <td>{location}</td>
             <td>{email}</td>
          </tr>
       )
    })
 }

 renderTableHeader() {
  let header = Object.keys(this.state.students[0])
  return header.map((key, index) => {
     return <th key={index}>{key.toUpperCase()}</th>
  })
}
   render() { 
      return (
         <div>
            <h1 id="title">Training Plan</h1>
            <div className="DataTable">
            <div className="ScrollContainer">
            <div>  
              <ReactTable  
                  students={students}  
                  student={student}  
                  defaultPageSize = {2}  
                  pageSizeOptions = {[2,4, 6]}  
              />  
              </div>
          </div>  
         </div>
         </div>
      )
   }
}

export default App;



