import Card from "./card";
import img1 from "./Pictures/img1.jpg";
import img2 from "./Pictures/img2.jpg";

class Cardimp extends Component {
    constructor(props){
        super(props)
        this.state={Text: "Nature",
    Text2: "Movies", Cast:"tony"}
    }

 render(){
        return ( 

            <div className="container">
            <div className="container-fluid d-flex justify-content-center">
                <div className="row">
                    <div className="col-md-6">
                        <Card imageSrc={img1} title="Nature" text={this.state.Text}/>
                    </div>

                </div>
                <div className="col-md-6">
                        <Card imageSrc={img2} title="Movies" text={this.state.Text2}/>
                    </div>
            </div>
            </div>
         );
    }
}

export default Cardimp;
