import React, { Component } from 'react';  
import ReactTable from "react-table";  
import "react-table/react-table.css"; 
import './App.css';


class App extends Component { 
  constructor() {
    super();
    this.state = {
      data:[
      { id: 1, name: 'Wasif', Business_unit: "Apps NA",Courses_Joined: 2, Course_name: "Java and Phython fundamentals",Course_status: "Nill",Experience: "3 Years",location: "bangalore",email: 'wasif@email.com'},
      { id: 2, name: 'Saleem', Business_unit: "Apps One", Courses_Joined: 1, Course_name: "Phython",Course_status: "50%",Experience: "5 Years",location: "Mumbai",email: 'ali@email.com' },
