            { id: 4, name: 'kumar', Business_unit: "NA",Courses_Joined: 4, Course_name: "JavaScript,venilaScript,vueJS and Angular",Course_status: "Nill",Experience: "3 Years", location: "Hydrabad", email: 'kumar@gmail.com' },
            { id: 5, name: 'Joseph', Business_unit: "APPS NA",Courses_Joined: 2, Course_name: "JavaScript and Angular",Course_status: "undergoing",Experience: "3 Years", location: "Hydrabad", email: 'joseph34@email.com' },
            { id: 6, name: 'Rahman', Business_unit: "NA",Courses_Joined: 1, Course_name: "spring and Hybernate",Course_status: "10%",Experience: "1 Years", location: "Hydrabad", email: 'rahmank@email.com' },
            { id: 7, name: 'Hari', Business_unit: "EU",Courses_Joined: 2, Course_name: "Java and spring",Course_status: "going on",Experience: "2 Years", location: "Mumbai", email: 'harirocky@email.com' },
            { id: 8, name: 'Anbhu', Business_unit: "NA",Courses_Joined: 1, Course_name: "Java",Course_status: "completed",Experience: "6 Years", location: "Hydrabad", email: 'anbukesar@email.com' },
            { id: 9, name: 'Ankit', Business_unit: "APPS ONE",Courses_Joined: 2, Course_name: "JavaScript and Angular",Course_status: "80%",Experience: "3 Years", location: "Chennai", email: 'ankit987@email.com' },
            { id: 10, name: 'Panchal', Business_unit: "EU",Courses_Joined: 3, Course_name: "JavaScript,typescript and Angular",Course_status: "100%",Experience: "4 Years", location: "Bangalore", email: 'panchal123@email.com' },
            { id: 11, name: 'A', Business_unit: "APPS ONE",Courses_Joined: 1, Course_name: " Angular",Course_status: "Completed",Experience: "3 Years", location: "Chennai", email: 'xyz@email.com' },
    ],
  };
}

  render() {
  const columns = [
      { Header: 'ID',accessor: 'id',},
       { Header: 'Name',accessor: 'name'},
       { Header: 'Business_unit',accessor: 'Business_unit'},
       { Header: 'Courses_Joined',accessor: 'Courses_Joined'},
       { Header: 'Course_name',accessor: 'Course_name'},
       { Header: 'Course_status',accessor: 'Course_status'},
       { Header: 'Experience',accessor: 'Experience'},
       { Header: 'location',accessor: 'location'},
       { Header: 'email',accessor: 'email'},
      ]  
    return (  
          <div className="table-header">
            <h1 align="center">Training Plan</h1>
            <div className="table">  
              <ReactTable 
                  data={this.state.data}  
                  columns={columns}  
                  defaultPageSize = {10}  

                  className="-striped -highlight"
                  style={
                    {
                      margin: "auto",
                      textAlign: "center",
                      width: "1200px",
                      border: "3px solid silver"
                    }
                  } 
              />
              <TablePagination pageSizeOptions = {[2,4, 6]}></TablePagination>
              </div>  
          </div>        
    );
                }  
  }  

export default App;
