import React from 'react';

const Posts = ({ posts, loading }) => {
  if (loading) {
    return <h2>Loading...</h2>;
  }

  return (
    <div className="DataTable">
      <table>
      {posts.map(post => (
        <tr key={post.id} className='table'>
          <td>{post.title}</td>
          <td>{post.body}</td>
        </tr>

      ))}</table>
    </div>
  );
};

export default Posts;
