import React from 'react';

const Posts = ({ posts, loading }) => {
  if (loading) {
    return <h2>Loading...</h2>;
  }

  return (
    <div className="DataTable">
      <table id="students">
        <tbody>
      {posts.map(post => (
        <tr key={post.id}>
          <td>{post.id}</td>
          <td>{post.title}</td>
          <td>{post.body}</td>
        </tr>

      ))}
      </tbody>
      </table>
    </div>
  );
};

export default Posts;
